mapper.select:
select count(*)
from comment
where bno = #{bno}

mapper.delete:
delete
from comment
where cno = #{cno} and commenter = #{commenter}

mapper.select:
select cno, bno, pcno, comment, commenter, reg_date, up_date
from comment
where cno = #{cno}

mapper.update:
update comment
    set comment = #{comment}
      , up_date = now()
where cno=#{cno} and commenter = #{commenter}
